import React from 'react';
import './index.scss';



class Form extends React.Component{

    constructor(props){

        super(props);

        this.state = {
            words: [],
            currentWord: "",
        }


        this.wordList = [];
        this.count = 0;

        this.handleEnter = this.handleEnter.bind(this);
        this.handleShow = this.handleShow.bind(this);

    }

    // componentDidMount() {        

