import React from 'react';
import './index.scss';


class Form extends React.Component{

    constructor(props){

        super(props);

        this.state = {
            words: [],
            currentWord: "",
        }

        this.wordList = [];
        this.count = 0;

        this.handleEnter = this.handleEnter.bind(this);
        this.handleShow = this.handleShow.bind(this);
        this.increaseCount = this.increaseCount.bind(this);
        this.setCurrentWord = this.setCurrentWord.bind(this);

    }

    componentWillUnmount() {
        clearInterval(this.timer);
    }

    handleEnter(){

        let word = document.querySelector(".field").value;
        document.querySelector(".field").value = "";
        this.wordList.push(word);
        this.setState({
            words: this.wordList
        })

   }

   increaseCount(){
                this.count++;
                if(this.count > this.state.words.length){
                   this.count = 0;
                }    
   }

   setCurrentWord(){
    this.setState({
        currentWord: this.state.words[this.state.count],
        count: this.count
    })
   }


//    Mount
    handleShow(){

        if(this.state.words.length < 3){
            this.setState({
                currentWord: "Expected at least 3 words or more"
            })
        }
        else{

            setInterval(() => {
                this.increaseCount();
                this.setCurrentWord();
               },1000);
        }
    }   

    handleSubmit(event){
        event.preventDefault();
        // Do stuff
    }

    render(){

    return (
        <article className="Form">

        <form onSubmit={this.handleSubmit} className="show">

        <fieldset>
            <label>Enter Word:</label>
            <input className="field" name="word"></input>
        </fieldset>

        <fieldset>
        <button onClick={this.handleShow}>Show</button>
        <button onClick={this.handleEnter}>Enter</button>
        </fieldset>

        </form>

        <section className="Results">
           I am a {this.state.currentWord} 
        </section>

        <section className="ErrorMsg">

        </section>

        </article>
      );

    }
}

export default Form;
